public class RemoteCloud_Impl implements Cloud  {
   //original object
   RemoteCloud originalObject;
   public java.lang.String getName() {
      try {
         return originalObject.getName();
      } catch (Exception e){throw new RuntimeException(e.getMessage());}
   }
