public interface StorageManagerFactory {

	public init(MMBase mmbase);

	public StorageManager getStorageManager();

	public Map getAttributes();

	public void setAttributes(Map attributes);

	public Object getAttribute(Object key);

	public void setAttribute(Object key, Object value);

}

public final class Storage {

    static public StorageManagerFactory getStorageManagerFactory(MMBase mmbase);

    static public StorageManagerFactory getStorageManagerFactory();

}

public class DatabaseStorageManagerFactory extends StorageManagerFactory {

	// StorageManagerFactory methods, plus:

	public boolean storesBinaryAsFile();
	public void storeBinaryAsFile(boolean value);

	public File getBinaryFilePath();
	public void setBinaryFilePath(File path);

	public Map getTypeMap();
	public void setTypeMap(Map typeMap);

	public Map getFieldNameMap();
	public void setFieldNameMap(Map fieldNameMap);

	public boolean canAlterStorage();
	public void setAlterStorage(boolean value);

	public Schemes getSchemes();
}
